package ejerciciostema1;

public class Ej04CambioMoneda {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double euros=30;
		double dolares=0;
		double conversion=0.78; // 1 dólar en euros
		
		dolares=euros/conversion;
				
		System.out.println(euros+" € = "+dolares+" $");
		
	}

}
